import './App.css';
import Header from "./components/header/Header";
import CardList from "./components/card_list/CardList";
import {useState} from "react";
import {ProductContext, ProductCountContext} from "./context/context";


function App() {
    const [cards, setCard] = useState([
        {
            id: 1,
            img: "./source/popular/game_img1.jpg",
            price: "1290 ₽",
            old_price: "1980 ₽",
            sale: -31,
            description: "Взрывные котята. Безумие, апокалипсис и котики",
            count: 1
        },
        {
            id: 2,
            img: "./source/popular/game_img2.jpg",
            price: "990 ₽",
            old_price: 0,
            sale: 0,
            description: "Манчкин 2 Дикий Топор. Ещё разрушительнее, ещё веселее!",
            count: 1
        },
        {
            id: 3,
            img: "./source/popular/game_img3.jpg",
            price: "1440 ₽",
            old_price: "1980 ₽",
            sale: -24,
            description: "Монополия Рик и Морти. Завяжи Мультивселеннуюв узел",
            count: 1
        },
        {
            id: 4,
            img: "./source/popular/game_img4.jpg",
            price: "1090 ₽",
            old_price: 0,
            sale: 0,
            description: "Зомби в доме. Дом на холме для детей",
            count: 1
        }
    ]);
    const [id, setId] = useState({id: ""}); // Состояние для увеличения кол-ва товаров

    return (
        <ProductContext.Provider value={{id, setId}}>
            <div className="App">
                <Header/>
                <CardList cards={cards}/>
            </div>
        </ProductContext.Provider>
    );
}

export default App;
