import React, {useEffect, useState} from 'react';
import CardCartList from "../card_list/CardCartList";
import ItogPrice from "../itog_price/ItogPrice";
import classes from "./cart_content.module.css";
import {useDispatch, useSelector} from "react-redux";

const CartContent = () => {
    // const dispath = useDispatch();
    const cart = useSelector(state => state.inCart.products);
    // let [allPrice, setAllPrice] = useState(0);
    //
    // const getPrice = (priceCard) => {
    //     setAllPrice(allPrice += priceCard)
    // }

    let [allCount, setAllCount] = useState(0);

    let [totalPrice, setTotalPrice] = useState(0);

    const allPrice = (price) => {
        setTotalPrice(totalPrice += price)
    }

    console.log(cart)



    useEffect(() => {
        allPrice();
    }, [allCount]);

    cart.forEach((i) => {
        setAllCount(allCount += i.count)
    })




    console.log(totalPrice)


    return (
        <div className={classes.container}>
            <CardCartList getPrice={allPrice}/>
            <ItogPrice/>
        </div>
    );
};

export default CartContent;
