import {ADD_PRODUCT} from "./case";

const defaultState = {
    products: [],
    count: 1
}
// Редюсер для добавления товара в корзину
export const inCartReducer = (state = defaultState, action) => {
    switch (action.type) {
        case ADD_PRODUCT:
            return {...state, products: [...state.products, action.payload]}
        default:
            return state
    };
};

export const addCount = (state = defaultState, action) => {
    switch (action.type) {
        case "ADD_COUNT":
            return {...state, count: action.payload}
        default:
            return state
    }
}
// Функция для добавления в редюсер
export const addInCart = (payload) => ({type: ADD_PRODUCT, payload});
