import React, {useState} from 'react';
import Input from "../UI/Input/Input";

const Form = () => {
    const [name, setName] = useState("");
    const [description, setDescription] = useState("");
    const [price, setPrice] = useState(0);
    const [old_price, setOldPrice] = useState(0);
    const [sale, setSale] = useState(0);

    console.log(name)
    return (
        <div>
            <h2>Создание нового товара</h2>
            <Input placeholder="Введите название" onChange={(e=>setName(e.target.value))}/>
            <Input placeholder="Введите описание"/>
            <Input placeholder="Введите новую цену"/>
            <Input placeholder="Введите старую цену"/>
            <Input placeholder="Введите скидку"/>

            <h3>Описание товара</h3>
            <Input placeholder="Введите название свойства"/>
            <Input placeholder="Введите описание свойства"/>
        </div>
    );
};

export default Form;
