import React, {useEffect, useState} from 'react';
import classes from "./Tab.module.css";

const Tab = ({props, active, tab, info, content}) => {
    const tabClass = [classes.item]

    const [activeTab, setActive] = useState(false);

    useEffect(() => {
        if (tab === props.id) {
            setActive(true)
        } else {
            setActive(false)
        }
    }, [tab])


    if (activeTab) {
        tabClass.push(classes.active)
    }

    // const getTab=()=>{
    //     setActive(true)
    // }

    const index = () => {
        active(props.id)
    }

    const [test, setTest] = useState([])
    const find = () => {
        const result = info.find(i => i.id === content)
        setTest(result)
    }

    useEffect(() => {
        find()
    }, [tab])

    return (
        <div>
            <div className={tabClass.join(" ")} onClick={index}>
                {props.title}
            </div>
            <div className="">{test.content}</div>
        </div>
    );
};

export default Tab;
