import {ADD_COUNT, ADD_ID, ADD_PRODUCT} from "./case";

const defaultState = {
    products: []
}
// Редюсер для добавления товара в корзину
export const inCartReducer = (state = defaultState, action) => {
    switch (action.type) {
        case ADD_PRODUCT:
            const item = state.products.find(n => n.id === action.payload.id);
            return {
                ...state,
                products: item
                    ? state.products.map(n => n === item ? { ...n, count: n.count + 1, price: n.price*n.count } : n)
                    : [ ...state.products, { ...action.payload, count: 1 } ],
            };
        case ADD_ID:
            return {...state, id: [...state.id, action.payload]}
        default:
            return state
    };
};

export const countReducer = (state = defaultState, action) => {
    switch (action.type) {
        case ADD_COUNT:
            return {...state, count: action.payload}
        default:
            return state
    }
};
// Функция для добавления в редюсер
export const addInCart = (payload) => ({type: ADD_PRODUCT, payload});
