import React, {useContext, useEffect} from 'react';
import {BrowserRouter, Route, Routes} from "react-router-dom";
import {useSelector} from "react-redux";
import {AuthContext} from "../context/context";
import {privateRoutes, publicRoutes} from "../routes/Routing";

const AppRouting = () => {
    const cards = useSelector(state => state.getCard.items);

    const {auth, setAuth} = useContext(AuthContext);
    useEffect(() => {
        if (localStorage.getItem("auth")) {
            setAuth(true)
        }
    })
// localStorage.clear()
    return (
        <BrowserRouter>
            <Routes>
                {auth ? privateRoutes.map(rout => <Route path={rout.path} element={rout.element} key={rout.id}/>)
                    : publicRoutes.map(rout => <Route path={rout.path} element={rout.element} key={rout.id}/>)}
                {/*<Route path="/" element={<Main/>}></Route>*/}
                {/*{cards.map(item => <Route path="products/:id" element={<InfoProduct/>}></Route>)}*/}
                {/*<Route path="/admin" element={<Admin/>}></Route>*/}
                {/*<Route path="/create" element={<CreateDevice/>}></Route>*/}
                {/*<Route path="*" element={<PageError/>}></Route>*/}

            </Routes>
        </BrowserRouter>
    );
};

export default AppRouting;
