import classes from "./Counter.module.css"

const Counter = ({props}) => {

    console.log(props)

    const [co, setCount]=useState(1)

    // const increment = () => {
    //     setCount(...props, count: co+1)
    // }

    const decrement = () => {
        if (count > 1) {
            setCount(count - 1)
        }
    }

    return (
        <div className={classes.counter}>
            <div className={classes.count_btn} onClick={increment}>+</div>
            <div className={classes.quantity}>{count}</div>
            <div className={classes.count_btn} onClick={decrement}>-</div>
        </div>
    );
};

export default Counter;
