import React, {useState} from 'react';
import classes from "./Counter.module.css"

const Counter = ({inc, props}) => {

    const increment = () => {
        inc({...props, count: 2})
    }

    // const decrement = () => {
    //     if (count > 1) {
    //         setCount(count - 1)
    //     }
    // }

    return (
        <div className={classes.counter}>
            <div className={classes.count_btn} onClick={increment}>+</div>
            <div className={classes.quantity}>{props.count}</div>
            <div className={classes.count_btn}>-</div>
        </div>
    );
};

export default Counter;
