import React from 'react';
import classes from "./ProductInfo.module.css"
import Button from "../basic/UI/button/Button";

const ProductInformation = ({info}) => {


    const IMG = `http://localhost:5000/${info.img}`;
    return (
        <div>
            <div className={classes.container}>
                <div className="">
                    <h1 style={{marginTop: 64}}>{info.name}</h1>
                    <div style={{marginTop: 24}}>{info.description}</div>
                    <img src={IMG} alt=""/>
                </div>
                <div className={classes.raiting}>
                    <div className="">10 отзывов</div>
                    <div className={classes.cost}>
                        <div className={classes.element_cost}>{info.price}</div>
                        <div className="">{info.old_price}</div>
                    </div>
                    <div className="">129 бонусов за покупку</div>
                    <Button>Купить</Button>
                    <div className="">Самовывоз из 11 пунктов выдачи, 2-36 дней, от 161 ₽</div>
                    <div className="">Почта России, от 3 дней, от 290 ₽</div>
                </div>
            </div>

        </div>
    );
};

export default ProductInformation;
