import React, {useEffect, useState} from 'react';
import CardCartList from "../card_list/CardCartList";
import ItogPrice from "../itog_price/ItogPrice";
import classes from "./cart_content.module.css";
import {useDispatch, useSelector} from "react-redux";
import {addInCart, decrementInCart} from "../../../../store/inCart";

const CartContent = () => {
    const dispath = useDispatch();
    const cart = useSelector(state => state.inCart.products);
    let [allPrice, setAllPrice] = useState(0);

    const getPrice = (priceCard) => {
        setAllPrice(allPrice += priceCard)
    }

    const incrementProduct = (product) => {
        dispath(addInCart(product))
    }

    const decrement = (product) => {
        dispath(decrementInCart(product))
    }


    return (
        <div className={classes.container}>
            <CardCartList price={getPrice} increment={incrementProduct} decrement={decrement}/>
            <ItogPrice/>
        </div>
    );
};

export default CartContent;
