import React, {useEffect, useState} from 'react';
import classes from "./Tab.module.css";

const Tab = ({props, active, tab, info, content}) => {
    const tabClass = [classes.item]

    const [activeTab, setActive] = useState(false);

    useEffect(() => {
        if (tab === props.id) {
            setActive(true)
        } else {
            setActive(false)
        }
    }, [tab])


    if (activeTab) {
        tabClass.push(classes.active)
    }

    // const getTab=()=>{
    //     setActive(true)
    // }

    const index = () => {
        active(props.id)
    }

    const [post, setPost] = useState("")

    const find = () => {
        const result = info.find(item => item.id === props.id)
        setPost(result)
    };

    useEffect(() => {
        find()
    }, [])

    console.log(post)


    return (
        <div>
            <div className={tabClass.join(" ")} onClick={index}>
                {props.title}
            </div>
            <div>{post}</div>
        </div>
    );
};

export default Tab;
