import React from 'react';
import classes from "./Tab.module.css";

const Tab = ({props, click}) => {
    const tabClass=[classes.item]

    if(click){
        tabClass.push(classes.active)
    }
    return (
            <div>
                <div className={tabClass.join(" ")} onClick={click}>
                    {props.title}
                </div>
                {/*{props.content}*/}
            </div>
    );
};

export default Tab;
