import React from 'react';
import classes from "./ModaleCart.module.css"
import Button from "../UI/button/Button";
import ItemCart from "./item_cart/ItemCart";
import {useDispatch, useSelector} from "react-redux";

const ModaleCart = () => {
    const dispath=useDispatch();
    const cart=useSelector(state => state.inCart.products)
    return (
        <div className={classes.modale_cart}>
            <div className={classes.modale_content}>
                <div className={classes.flex_container}>
                    <span className={classes.title}>Корзина</span>
                    <button className={classes.clear_cart}>Очистить</button>
                </div>
                <div className={classes.items_container}>
                    {cart.map(item=><ItemCart props={item}/>)}
                </div>
                <div className="price_cart_itog">
                    <div className="text_itog flex_container">
                        <span className="text_result title">Итого:</span>
                        <span className="text_result title"
                        ><span className="itog_price">0</span> ₽</span
                        >
                    </div>
                    <Button>Оформить заказ</Button>
                </div>
            </div>
        </div>
    );
};

export default ModaleCart;
