import './App.css';
import Header from "./components/header/Header";
import Card from "./components/card/Card";
import CardList from "./components/card_list/CardList";
import {useState} from "react";

const [cards, setCard] = useState([
    {
        "id": 1,
        "img": "game_img1.jpg",
        "price": "1290 ₽",
        "old_price": "1980 ₽",
        "sale": "-31%",
        "description": "Взрывные котята. Безумие, апокалипсис и котики"
    },
    {
        "id": 2,
        "img": "game_img2.jpg",
        "price": "990 ₽",
        "old_price": "0 ₽",
        "sale": 0,
        "description": "Манчкин 2 Дикий Топор.Ещё разрушительнее, ещё веселее!"
    },
    {
        "id": 3,
        "img": "game_img3.jpg",
        "price": "1440 ₽",
        "old_price": "1980 ₽",
        "sale": "-24%",
        "description": "Монополия Рик и Морти.Завяжи Мультивселеннуюв узел"
    },
    {
        "id": 4,
        "img": "game_img4.jpg",
        "price": "1090 ₽",
        "old_price": "0 ₽",
        "sale": 0,
        "description": "Зомби в доме. Дом на холме для детей"
    }
])

function App() {
    return (
        <div className="App">
            <Header/>
            <CardList cards={cards}/>
        </div>
    );
}

export default App;
