import React, {useEffect} from 'react';
import {useDispatch, useSelector} from "react-redux";
import CardCart from "../card/CardCart";

const CardCartList = ({getPrice}) => {
    const cart = useSelector((state) => state.inCart.products);

    const total = () => {
        let price = 0;
        let quantity = 0;
        cart.forEach(i => {
            price += i.count * i.price
            quantity += i.count
        })
        return {price, quantity}
    }

    return (
        <div>
            {cart.map(element => <CardCart props={element} getPrice={getPrice}/>)}
            <h1>{total().price}:{total().quantity}</h1>
        </div>
    );
};

export default CardCartList;
