import {createSlice} from "@reduxjs/toolkit";

const test = createSlice({
    name: "toolkit", initialState: {
        products: []
    }, reducers: {
        addInCart(state, action) {
            state.products.push({...action.payload, count: 1})
        },
        increment(state, action) {
            const item = state.products.find(i => i.id === action.payload.id);
            item.count++
        },
        removeItem(state,action){
            state.products.filter(i=>i.id !==action.payload.id)
        }
    }
})

export const red=test.reducer
export const {addInCart, increment, removeItem} = test.actions
