import React from 'react';
import classes from "./ProductInfo.module.css"

const ProductInformation = ({info}) => {


    const IMG = `http://localhost:5000/${info.img}`;
    return (
        <div>
            <div className={classes.container}></div>
            <div className=""><h1 style={{marginTop: 64}}>{info.name}</h1>
                <div style={{marginTop: 24}}>{info.description}</div>
                <img src={IMG} alt=""/>
            </div>
            <div className={classes.raiting}>
                <div className="">10 отзывов</div>
                <div className={classes.cost}>
                    <div className={classes.element_cost}>{info.price}</div>
                    <div className="">{info.old_price}</div>
                </div>

            </div>

        </div>
    );
};

export default ProductInformation;
