import './App.css';
import Header from "./components/header/Header";
import CardList from "./components/card_list/CardList";
import {useState} from "react";
import {useSelector} from "react-redux";


function App() {
    const [search, setSearch] = useState("");
    // const cards = useSelector(state => state.getCard.items)
    const [cards, setCards]=useState([
        {
            id: 1,
            img: "./source/popular/game_img1.jpg",
            price: 1290,
            old_price: "1980 ₽",
            sale: -31,
            description: "Взрывные котята. Безумие, апокалипсис и котики",

        },
        {
            id: 2,
            img: "./source/popular/game_img2.jpg",
            price: 990,
            old_price: 0,
            sale: 0,
            description: "Манчкин 2 Дикий Топор. Ещё разрушительнее, ещё веселее!",

        },
        {
            id: 3,
            img: "./source/popular/game_img3.jpg",
            price: 1440,
            old_price: "1980 ₽",
            sale: -24,
            description: "Монополия Рик и Морти. Завяжи Мультивселеннуюв узел",

        },
        {
            id: 4,
            img: "./source/popular/game_img4.jpg",
            price: 1090,
            old_price: 0,
            sale: 0,
            description: "Зомби в доме. Дом на холме для детей",

        }
    ])
    console.log(cards)
    const findSearch = () => {
        if(search){
            return cards.filter((card) => card.title.toLowerCase().includes(search.toLowerCase()))
        }
        else {
            return cards
        }
    };
    return (
        <div className="App">
            <Header setSearch={setSearch}/>
            <CardList props={findSearch()}/>
        </div>
    );
}

export default App;
