import React from 'react';
import {useDispatch, useSelector} from "react-redux";
import CardCart from "../card/CardCart";
import {increment} from "../../../../store/inCart";

const CardCartList = ({price}) => {

    const dispath = useDispatch()
    const cart = useSelector((state) => state.inCart.products);

    const count = useSelector(state => state.inCart.count);

    const add = () => {
        dispath(increment())
    }

    console.log(cart)
    return (
        <div>
            {cart.map(element => <CardCart props={element} getPrice={price} count={count} increment={add}/>)}
        </div>
    );
};

export default CardCartList;
