import React, {useEffect, useState} from 'react';
import classes from "./CardCart.module.css";
import Counter from "../../counter/Counter";
import Button from "../../../basic/UI/button/Button";
import {useDispatch, useSelector} from "react-redux";
import {decrement, increment} from "../../../../store/inCart";


const CardCart = ({props, getPrice, count, increment}) => {
    const img = `http://localhost:5000/${props.img}`;

    const [price, setPrice] = useState(0);


    const dispath = useDispatch();

    const dec = () => {
        dispath(decrement())
    }

    // const [count, setCount] = useState(props.count);
    //
    // const itogCost = () => {
    //     setPrice(count * props.price)
    // };

    // useEffect(() => {
    //     itogCost()
    //     getPrice(price)
    // }, [count]);

    console.log(count)

    return (
        <div className={classes.container}>
            <div className={classes.card}>
                <img src={img} alt="" className={classes.img}/>
                <div className={classes.title}>{props.name}</div>
                <Button onClick={increment}>+</Button>
                <Button onClick={dec}>-</Button>
                <h1 className="">{count}</h1>
                {/*<Counter count={count} setCount={setCount}/>*/}
                <div className={classes.price}>{props.price} ₽</div>
                {props.old_price !== 0 && <div className={classes.old_price}>{props.old_price} ₽</div>}
            </div>
        </div>
    );
};

export default CardCart;
